#pg-header div,
#pg-footer div {
  all: initial;
  display: block;
  margin-top: 1em;
  margin-bottom: 1em;
  margin-left: 2em;
}

#pg-footer div.agate {
  font-size: 90%;
  margin-top: 0;
  margin-bottom: 0;
  text-align: center;
}

#pg-footer li {
  all: initial;
  display: block;
  margin-top: 1em;
  margin-bottom: 1em;
  text-indent: -0.6em;
}

#pg-footer div.secthead {
  font-size: 110%;
  font-weight: bold;
}

#pg-footer #project-gutenberg-license {
  font-size: 110%;
  margin-top: 0;
  margin-bottom: 0;
  text-align: center;
}

#pg-header-heading {
  all: inherit;
  text-align: center;
  font-size: 120%;
  font-weight: bold;
}

#pg-footer-heading {
  all: inherit;
  text-align: center;
  font-size: 120%;
  font-weight: normal;
  margin-top: 0;
  margin-bottom: 0;
}

#pg-header #pg-machine-header p {
  text-indent: -4em;
  margin-left: 4em;
  margin-top: 1em;
  margin-bottom: 0;
  font-size: medium
}

#pg-header #pg-header-authlist {
  all: initial;
  margin-top: 0;
  margin-bottom: 0;
}

#pg-header #pg-machine-header strong {
  font-weight: normal;
}

#pg-header #pg-start-separator,
#pg-footer #pg-end-separator {
  margin-bottom: 3em;
  margin-left: 0;
  margin-right: auto;
  margin-top: 2em;
  text-align: center
}

.xhtml_center {
  text-align: center;
  display: block;
}

.xhtml_center table {
  display: table;
  text-align: left;
  margin-left: auto;
  margin-right: auto;
}

@media screen {
  body {
    margin-left: 10%;
    margin-right: 10%
  }
}

.pagedjs_page_content>div {
  margin-left: 10%;
  margin-right: 10%
}

h1,
h2 {
  text-align: center;
  clear: both
}

p {
  margin-top: 0.51em;
  text-align: justify;
  margin-bottom: 0.49em
}

div.chapter {
  page-break-before: always
}

h2.nobreak {
  page-break-before: avoid
}

div.titlepage {
  text-align: center;
  page-break-before: always;
  page-break-after: always
}

div.titlepage p {
  text-align: center;
  font-weight: bold;
  line-height: 1.5;
  margin-top: 2em;
  margin-bottom: 2em
}

hr {
  width: 33%;
  margin-top: 2em;
  margin-bottom: 2em;
  margin-left: 33.5%;
  margin-right: 33.5%;
  clear: both
}

hr.chap {
  width: 65%;
  margin-left: 17.5%;
  margin-right: 17.5%
}

hr.tb {
  width: 45%;
  margin-left: 27.5%;
  margin-right: 27.5%
}

table {
  margin-left: auto;
  margin-right: auto
}

.tdr {
  text-align: right
}

.tdc {
  text-align: center
}

.pagenum {
  position: absolute;
  left: 92%;
  font-size: smaller;
  text-align: right
}

.blockquot {
  margin-left: 15%;
  margin-right: 15%
}

.xxlarge {
  font-size: 175%
}

.xlarge {
  font-size: 150%
}

.large {
  font-size: 125%
}

.bbox {
  border: solid 2px;
  margin-left: 20%;
  margin-right: 20%
}

.center {
  text-align: center
}

.right {
  text-align: right
}

.smcap {
  font-variant: small-caps
}

.u {
  text-decoration: underline
}

.gap {
  padding-left: 5em
}

.ph1 {
  text-align: center;
  font-size: large;
  font-weight: bold
}

.figcenter {
  margin: auto;
  text-align: center
}

.poetry-container {
  text-align: center
}

.poetry {
  display: inline-block;
  text-align: left
}

.poetry .verse {
  text-indent: -2.5em;
  padding-left: 3em
}

.transnote {
  background-color: #E6E6FA;
  color: black;
  font-size: smaller;
  padding: 0.5em;
  margin-bottom: 5em;
  font-family: sans-serif, serif
}
